import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    
    axios.post('http://localhost:3025/login', { email, password })
      .then(response => {
        if (response.data.status === "Success") {
          alert(`Welcome, ${response.data.name}`); // Fixed syntax here
          navigate('/user-form'); // Navigate to the UserForm component
        } else {
          alert(response.data.message);  // Display error messages like 'incorrect password' or 'no record found'
        }
      })
      .catch(error => console.error('There was an error!', error));
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      <p>Don't have an account? <a href="/signup">Sign Up</a></p>
    </div>
  );
};

export default Login;
