import React, { useState } from 'react';
import axios from 'axios';

const Rform = () => {
  const [formData, setFormData] = useState({});
  const [score, setScore] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    try {
      const response = await axios.post('http://localhost:6002/api/submit-user-details', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.data.status === 'Success') {
        setScore(response.data.score); // Set score from response
      } else {
        alert('Submission failed');
      }
    } catch (error) {
      console.error('There was an error!', error.response ? error.response.data : error.message);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prevState => ({ ...prevState, [name]: value }));
  };

  return (
    <div>
      <h2>Resume Form</h2>
      <form onSubmit={handleSubmit}>
        {/* Your form fields here */}
        <input type="text" name="exampleField" onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>
      {score !== null && <p>Your score: {score}</p>} {/* Display the score */}
    </div>
  );
};

export default Rform;
