import React, { useState } from 'react';
import axios from 'axios';

const ResumeForm = () => {
  const [formData, setFormData] = useState({ name: '', email: '', resume: null });

  const handleChange = (event) => {
    const { name, value, files } = event.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: name === 'resume' ? files[0] : value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    const formDataToSend = new FormData();
    formDataToSend.append('name', formData.name);
    formDataToSend.append('email', formData.email);
    formDataToSend.append('resume', formData.resume);

    try {
      const response = await axios.post('http://localhost:6002/api/submit-user-details', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      alert(response.data.message);
    } catch (error) {
      console.error('There was an error!', error);
      alert('Failed to execute job-board-app');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />
      </div>
      <div>
        <label>Email:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required />
      </div>
      <div>
        <label>Resume:</label>
        <input type="file" name="resume" onChange={handleChange} required />
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default ResumeForm;
