import React, { useState } from 'react';
import axios from 'axios';
import BackButton from './BackButton'; // Import the BackButton component
import './ContactPage.css'; // Import the CSS file for styling

const ContactPage = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    
    // Prepare the WhatsApp URL
    const whatsappUrl = `https://wa.me/<9043856464>?text=${encodeURIComponent('Name: ${name}\nEmail: ${email}\nMessage: ${message}')}`;

    // You can still send the message to your server if needed
    axios.post('http://localhost:3007/contact', { name, email, message })
      .then(response => {
        console.log('Message sent:', response);
        setStatus('Your message has been sent successfully!');
        setName('');
        setEmail('');
        setMessage('');
      })
      .catch(error => {
        console.error('There was an error!', error);
        setStatus('Failed to send the message. Please try again.');
      });

    // Open WhatsApp with the pre-filled message
    window.open(whatsappUrl, '_blank');
  };

  return (
    <div className="contact-page">
      <BackButton /> {/* Include the BackButton component */}
      <div className="contact-content">
        <h1>Contact Us</h1>
        <form onSubmit={handleSubmit} className="contact-form">
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Message:</label>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="submit-button">Send</button>
        </form>
        {status && <p className="status-message">{status}</p>}
      </div>
    </div>
  );
};

export default ContactPage;
