import React, { useState } from 'react';
import axios from 'axios';

const ResumeForm = () => {
  const [data, setData] = useState(new FormData());

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Submit resume data
      await axios.post('http://localhost:6002/api/submit-user-details', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Trigger additional action for job-board-app
      await axios.post('http://localhost:6002/api/run-job-board-app');

      alert('Form submitted and job-board-app triggered successfully!');
    } catch (error) {
      console.error('There was an error!', error.response ? error.response.data : error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Your form fields here */}
      <button type="submit">Submit</button>
    </form>
  );
};

export default ResumeForm;
