import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('http://localhost:3031/login', { email, password })
      .then(response => {
        console.log(response);  // Inspect the response structure

        if (response.data && response.data.status === "Success") {
          const name = response.data.name || 'User'; // Default to 'User' if name is undefined
          alert(`Welcome, ${name}`);
          navigate('/user-form'); // Navigate to the UserForm component
        } else if (response.data && response.data.message) {
          alert(response.data.message);
        } else {
          alert('Unexpected error. Please try again.');
        }
      })
      .catch(error => {
        console.error('There was an error!', error);
        alert('An error occurred. Please try again later.');
      });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      <p>Don't have an account? <a href="/signup">Sign Up</a></p>
    </div>
  );
};

export default Login;
