import React from 'react';
import { useNavigate } from 'react-router-dom';
import './EntryPage.css'; // Import the CSS file for styling

const EntryPage = () => {
  const navigate = useNavigate();

  return (
    <div className="entry-page">
      <header className="header">
        <nav>
          <button onClick={() => navigate('/about')} className="nav-button">About Us</button>
          <button onClick={() => navigate('/contact')} className="nav-button">Contact Us</button>
        </nav>
      </header>
      <div className="content">
        <img src="c:\Users\HP\OneDrive\Pictures\images.jpg" alt="Logo" className="logo" />
        <h1>Welcome to HireX</h1>
        <video className="intro-video" controls>
          <source src="C:\Users\HP\Videos\Captures\BMI 2024-05-05 23-10-47.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <button onClick={() => navigate('/login')} className="login-button">Login</button>
      </div>
    </div>
  );
};

export default EntryPage;
