import React, { useState } from 'react';
import axios from 'axios';

const Rform = () => {
  const [formData, setFormData] = useState({
    name: '',
    dob: '',
    education: '',
    experience: '',
    email: '',
    phone: '',
    resume: null,
  });
  const [score, setScore] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      resume: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('name', formData.name);
    data.append('dob', formData.dob);
    data.append('education', formData.education);
    data.append('experience', formData.experience);
    data.append('email', formData.email);
    data.append('phone', formData.phone);
    if (formData.resume) {
      data.append('resume', formData.resume);
    }

    try {
      const response = await axios.post('http://localhost:3007/api/submit-user-details', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.status === 'Success') {
        setScore(response.data.score); // Set score from response
      } else {
        alert('Submission failed');
      }
    } catch (error) {
      console.error('There was an error!', error.response ? error.response.data : error.message);
    }
  };

  return (
    <div>
      <h2>Resume Submission Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Date of Birth:</label>
          <input type="date" name="dob" value={formData.dob} onChange={handleChange} required />
        </div>
        <div>
          <label>Education Qualification:</label>
          <input type="text" name="education" value={formData.education} onChange={handleChange} required />
        </div>
        <div>
          <label>Work Experience (Years):</label>
          <input type="number" name="experience" value={formData.experience} onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone Number:</label>
          <input type="text" name="phone" value={formData.phone} onChange={handleChange} required />
        </div>
        <div>
          <label>Resume (PDF):</label>
          <input type="file" name="resume" accept="application/pdf" onChange={handleFileChange} required />
        </div>
        <button type="submit">Submit</button>
      </form>
      {score !== null && <p>Your score: {score}</p>} {/* Display the score */}
    </div>
  );
};

export default Rform;
